import {
  ComponentProps,
  withStreamlitConnection,
} from "streamlit-component-lib"
// import { useRenderData } from "streamlit-component-lib-react-hooks"
import React, { useRef } from "react"
import { toPng } from "html-to-image"
import { TreeDataContext } from "./TreeDataContext"
import Node from "./Node"
import "./Node.scss"

const Tree = (props: ComponentProps) => {
  const args = props.args
  const key = args.key

  const treeRef = useRef(null)

  const treeToPng = (ref: any) => {
    toPng(ref.current, { cacheBust: false })
      .then((dataUrl) => {
        const link = document.createElement("a")
        link.download = `${key}.png`
        link.href = dataUrl
        link.click()
      })
      .catch((err) => {
        console.log(err)
      })
    toPng(document.getElementById("my-node")).then(function (dataUrl) {
      download(dataUrl, "my-node.png")
    })
  }

  // const theme = args.theme
  // const style: React.CSSProperties = {}

  return (
    <TreeDataContext.Provider value={args}>
      <button onClick={(e) => treeToPng(treeRef)}>Save as Image</button>
      <div className="tree">
        <ul className="rootNode">
          <li>
            <Node id={0} />
          </li>
        </ul>
      </div>
    </TreeDataContext.Provider>
  )
}

export default withStreamlitConnection(Tree)
