import { useState, useContext, useEffect } from "react"
import { TreeDataContext } from "./TreeDataContext"
import { Streamlit } from "streamlit-component-lib"

import NodeContent from "./NodeContent"
import EdgeContent from "./EdgeContent"

export interface NodeObject {
  node_id: number
  feature: string
  threshold: number
  impurity: number
  n_node_samples: number
  left: number
  right: number
  value: number[]
  color: string
}

export interface NodeId {
  id: number
}

const Node = ({ id }: NodeId) => {
  const args = useContext(TreeDataContext)
  const data: NodeObject[] = args!.data
  const [childrenVisible, setChildrenVisible] = useState(true)

  useEffect(() => Streamlit.setFrameHeight(), [childrenVisible])

  const node_data = data.find((a) => a.node_id === id)

  const hasLeftChildren = node_data!.left !== -1
  const hasRightChildren = node_data!.right !== -1
  const hasChildren = hasLeftChildren && hasRightChildren

  function switchChildrenVisible() {
    setChildrenVisible(!childrenVisible)
  }

  return (
    <>
      <NodeContent id={id} />
      {hasChildren && (
        <div
          className="child-button"
          onClick={(e) => switchChildrenVisible()}
        ></div>
      )}

      {hasChildren && childrenVisible && (
        <ul>
          {hasLeftChildren && (
            <li>
              <EdgeContent id={id} isLeft={true} />
              <Node id={node_data!.left} />
            </li>
          )}
          {hasRightChildren && (
            <li>
              <EdgeContent id={id} isLeft={false} />
              <Node id={node_data!.right} />
            </li>
          )}
        </ul>
      )}
    </>
  )
}

export default Node
