import { useContext } from "react"
import { TreeDataContext } from "./TreeDataContext"

export interface NodeObject {
  node_id: number
  feature: string
  threshold: number
  impurity: number
  n_node_samples: number
  left: number
  right: number
  value: number[]
}

interface NodeId {
  id: number
  type: string
}

const Node = ({ id, type }: NodeId) => {
  const data: NodeObject[] = useContext(TreeDataContext)

  const node_data = data.find((a) => a.node_id === id)
  const content_node = `${node_data!.node_id} | ${node_data!.value}`
  const content_edge_left = `${node_data!.feature} - ${node_data!.threshold}`
  const content_edge_right = `${node_data!.feature} - ${node_data!.threshold}`

  return (
    <>
      <div className="node">{content_node}</div>

      {node_data!.left !== -1 && (
        <>
          <div className="node">{content_edge_left}</div>
          <Node id={node_data!.left} type="node" />
        </>
      )}
      {node_data!.right !== -1 && (
        <>
          <div className="node">{content_edge_right}</div>
          <Node id={node_data!.right} type="node" />
        </>
      )}
    </>
  )
}

export default Node
