import { useContext } from "react"
import { TreeDataContext } from "./TreeDataContext"
import type { NodeObject, NodeId } from "./Node"

const NodeContent = ({ id }: NodeId) => {
  const data: NodeObject[] = useContext(TreeDataContext)
  const node_data = data.find((a) => a.node_id === id)

  return (
    <>
      <div className="node">
        <div className="node-content-left" style={{ backgroundColor: "black" }}>
          {node_data!.node_id}
        </div>
        <div className="node-content-right">
          <div className="node-content-right-item">
            {node_data!.n_node_samples}
          </div>
          <div className="node-content-right-item">
            {node_data!.value.join(", ")}
          </div>
        </div>
      </div>
    </>
  )
}

export default NodeContent
