import { useState, useContext } from "react"
import { TreeDataContext } from "./TreeDataContext"
import "./Node.scss"

export interface NodeObject {
  node_id: number
  feature: string
  threshold: number
  impurity: number
  n_node_samples: number
  left: number
  right: number
  value: number[]
}

interface NodeId {
  id: number
  type: string
}

const Node = ({ id }: NodeId) => {
  const data: NodeObject[] = useContext(TreeDataContext)
  const [childrenVisible, setChildrenVisible] = useState(false)

  const node_data = data.find((a) => a.node_id === id)
  const content_node = `${node_data!.node_id} | ${node_data!.value}`
  const content_edge_left =
    node_data!.left === -1
      ? null
      : `${node_data!.feature} <= ${node_data!.threshold.toFixed(2)}`
  const content_edge_right =
    node_data!.right === -1
      ? null
      : `${node_data!.feature} > ${node_data!.threshold.toFixed(2)}`

  return (
    <>
      <li>
        <a href="#" className="node">
          {content_node}
        </a>

        {node_data!.left + node_data!.right !== -2 && (
          <ul>
            {node_data!.left !== -1 && (
              <>
                <li>
                  {/* <div className="edge">{content_edge_left}</div> */}
                  <Node id={node_data!.left} type="node" />
                </li>
              </>
            )}
            {node_data!.right !== -1 && (
              <>
                <li>
                  {/* <div className="edge">{content_edge_right}</div> */}
                  <Node id={node_data!.right} type="node" />
                </li>
              </>
            )}
          </ul>
        )}
      </li>
    </>
  )
}

export default Node
