import {
  ComponentProps,
  withStreamlitConnection,
} from "streamlit-component-lib"
// import { useRenderData } from "streamlit-component-lib-react-hooks"
import React, { useRef } from "react"
import downloadjs from "downloadjs"
import html2canvas from "html2canvas"
import { TreeDataContext } from "./TreeDataContext"
import Node from "./Node"
import "./Node.scss"

const Tree = (props: ComponentProps) => {
  const args = props.args
  const key = args.key

  const treeRef = useRef(null)

  const treeToPng = async (ref: any) => {
    const canvas = await html2canvas(ref)
    const dataURL = canvas.toDataURL("image/png")
    downloadjs(dataURL, `${key}.png`, "image/png")
  }

  // const theme = args.theme
  // const style: React.CSSProperties = {}

  return (
    <TreeDataContext.Provider value={args}>
      <button onClick={(e) => treeToPng(treeRef)}>Download as Image</button>
      <div className="tree" ref={treeRef}>
        <ul className="rootNode">
          <li>
            <Node id={0} />
          </li>
        </ul>
      </div>
    </TreeDataContext.Provider>
  )
}

export default withStreamlitConnection(Tree)
