import { Streamlit } from "streamlit-component-lib"
import { useRenderData } from "streamlit-component-lib-react-hooks"
import React, { useState, useEffect, useCallback } from "react"

interface NodeObject {
  node_id: number
  feature: string
  threshold: number
  impurity: number
  n_node_samples: number
  left: number
  right: number
  value: number[]
}

const MyComponent: React.VFC = () => {
  // "useRenderData" returns the renderData passed from Python.
  const renderData = useRenderData()
  const name = renderData.args["name"]
  const data = renderData.args["data"]
  const height = renderData.args["height"]

  const [frameHeight, setFrameHeight] = useState(Streamlit.lastFrameHeight)

  console.log("", name, data)

  useEffect(() => {
    setFrameHeight(height)
    Streamlit.setFrameHeight(frameHeight)
    console.log("", name, frameHeight, eamlit.lastFrameHeight)
  }, [height, frameHeight])

  const theme = renderData.theme
  const style: React.CSSProperties = {}

  return (
    <div className="">
      <h1>{name}</h1>
      <Node {...data[0]}></Node>
      {Streamlit.setFrameHeight(height)}
    </div>
  )
}

const Node = (props: NodeObject) => {
  console.log("", props)
  return <div></div>
}

export default MyComponent
