import { useState, useContext, useEffect } from "react"
import { TreeDataContext } from "./TreeDataContext"
import { Streamlit } from "streamlit-component-lib"
import "./Node.scss"

export interface NodeObject {
  node_id: number
  feature: string
  threshold: number
  impurity: number
  n_node_samples: number
  left: number
  right: number
  value: number[]
}

interface NodeId {
  id: number
  type: string
}

const Node = ({ id }: NodeId) => {
  const data: NodeObject[] = useContext(TreeDataContext)
  const [childrenVisible, setChildrenVisible] = useState(true)

  useEffect(() => Streamlit.setFrameHeight(), [childrenVisible])

  const node_data = data.find((a) => a.node_id === id)
  const content_node = `${node_data!.node_id} | ${node_data!.n_node_samples}:${
    node_data!.value
  }`

  const hasLeftChildren = node_data!.left !== -1
  const hasRightChildren = node_data!.right !== -1
  const hasChildren = hasLeftChildren && hasRightChildren

  const content_edge_left = hasLeftChildren
    ? `${node_data!.feature} <= ${node_data!.threshold.toFixed(2)}`
    : null
  const content_edge_right = hasRightChildren
    ? `${node_data!.feature} > ${node_data!.threshold.toFixed(2)}`
    : null

  function switchChildrenVisible() {
    setChildrenVisible(!childrenVisible)
  }

  return (
    <>
      <div className="node">{content_node}</div>
      {hasChildren && (
        <div
          className="child-button"
          onClick={(e) => switchChildrenVisible()}
        ></div>
      )}

      {hasChildren && childrenVisible && (
        <ul>
          {hasLeftChildren && (
            <>
              <li>
                <div className="edge">{content_edge_left}</div>
                <Node id={node_data!.left} type="node" />
              </li>
            </>
          )}
          {hasRightChildren && (
            <>
              <li>
                <div className="edge">{content_edge_right}</div>
                <Node id={node_data!.right} type="node" />
              </li>
            </>
          )}
        </ul>
      )}
    </>
  )
}

export default Node
