import { useContext } from "react"
import { TreeDataContext } from "./TreeDataContext"

export interface NodeObject {
  node_id: number
  feature: string
  threshold: number
  impurity: number
  n_node_samples: number
  left: number
  right: number
  value: number[]
}

interface NodeId {
  id: number
}

const Node = ({ id }: NodeId) => {
  const data: NodeObject[] = useContext(TreeDataContext)

  const node_data = data.find((a) => a.node_id === id)

  console.log("", id)
  return (
    <>
      <div className="node">{`${node_data?.node_id} | ${node_data?.feature} - ${node_data?.threshold} : ${node_data?.value}`}</div>
      {node_data?.left !== -1 && <Node id={node_data.left} />}
    </>
  )
}

export default Node
