import { useContext } from "react"
import { TreeDataContext } from "./TreeDataContext"

export interface NodeObject {
  node_id: number
  feature: string
  threshold: number
  impurity: number
  n_node_samples: number
  left: number
  right: number
  value: number[]
}

interface NodeId {
  id: number
  type: string
}

const Node = ({ id, type }: NodeId) => {
  const data: NodeObject[] = useContext(TreeDataContext)

  const node_data = data.find((a) => a.node_id === id)

  console.log("", id)
  return (
    <>
      <div className="node">
        {type === "node" &&
          `${node_data!.node_id} | ${node_data!.value} | ${
            node_data!.feature
          } - ${node_data!.threshold}`}
      </div>

      {node_data!.left !== -1 && (
        <>
          <Node id={node_data!.left} type="node" />{" "}
          <Node id={node_data!.left} type="node" />
        </>
      )}
      {node_data!.right !== -1 && (
        <>
          <Node id={node_data!.right} type="node" />
          <Node id={node_data!.right} type="node" />
        </>
      )}
    </>
  )
}

export default Node
