import {
  ComponentProps,
  Streamlit,
  withStreamlitConnection,
} from "streamlit-component-lib"
import React, { useEffect, useState, useRef } from "react"
import ReactDOM from "react-dom"

import downloadjs from "downloadjs"
import html2canvas from "html2canvas"
import { ArgsContext } from "./ArgsContext"
import Node from "./Node"
import "./Node.scss"

const setStyle = (attr: string, value: string) => {
  document.getElementById("root")!.style.setProperty(attr, value)
}

const Tree = (props: ComponentProps) => {
  const args = props.args
  const key = args.key
  const [lastFrameHeight, setLastFrameHeight] = useState(0)
  const treeRef: any = useRef(null)

  setStyle("--font-family", "arial")
  setStyle("--font-size", "0.7em")
  setStyle("--max-height", "2400px")
  setStyle("--edge-size", "150px")
  setStyle("--node-size", "10px")
  setStyle("--padding-quantum", "5px")
  setStyle("--edge-color", "#ccc")
  setStyle("--edge-hover-color", "#94a0b4")
  setStyle("--node-hover-color", "#c8e4f8")
  setStyle("--node-border-color", "#ccc")
  setStyle("--text-color", "#333")
  setStyle("--text-hover-color", "#333")
  setStyle("--button-color", "rgb(185, 145, 145)")
  setStyle("--button-hover-color", "rgb(150, 0, 0)")

  useEffect(() => {
    const intervalId = setInterval(() => {
      if (treeRef.current != null) {
        setLastFrameHeight(treeRef.current!.clientHeight)
      }
    }, 20)
    Streamlit.setFrameHeight()
    // console.log(
    //   lastFrameHeight,
    //   Streamlit.lastFrameHeight,
    //   treeRef.current!.clientHeight
    // )
    return () => clearInterval(intervalId)
  }, [lastFrameHeight])

  const treeToPng = async () => {
    const element = document.querySelector(".tree")!
    const useWidth = element.scrollWidth
    const canvas = await html2canvas(document.querySelector(".tree")!, {
      width: useWidth,
      windowWidth: useWidth,
    })
    const dataURL = canvas.toDataURL("image/jpg")
    downloadjs(dataURL, `${key}.jpg`, "image/jpg")
  }

  // const theme = args.theme
  // const style: React.CSSProperties = {}

  return (
    <ArgsContext.Provider value={args}>
      <button onClick={(e) => treeToPng()}>Download as Image</button>
      <div className="tree" ref={treeRef}>
        <ul className="rootNode">
          <li>
            <Node id={0} />
          </li>
        </ul>
      </div>
      <br />
    </ArgsContext.Provider>
  )
}

export default withStreamlitConnection(Tree)
