import { createContext, useContext, useState } from "react"

const default_args: any = {}
export const ArgsContext = createContext(default_args)

interface ArgsProviderProps {
  children: any
}

export const ArgsProvider = ({ children }: ArgsProviderProps) => {
  const [currentArgs, setCurrentArgs] = useState(default_args)

  const { args } = useContext(ThemeContext)

  const updateArgs = (args: any) => {
    setCurrentArgs(args)
  }

  return (
    <ArgsContext.Provider value={{ settings: currentArgs, updateArgs }}>
      {children}
    </ArgsContext.Provider>
  )
}
